// Root component\n<template>\n  <div>\n    <LessonCard v-for=\"mod in modules\" :key=\"mod.id\" :title=\"mod.title\" @unlock=\"unlock(mod.id)\" />\n    <ProgressIndicator :completed=\"completed\" :total=\"modules.length\" />\n    <StreakChart :data=\"chartData\" />\n  </div>\n</template>\n<script setup>\nimport { ref, computed } from 'vue';\nimport LessonCard from './components/LessonCard.vue';\nimport ProgressIndicator from './components/ProgressIndicator.vue';\nimport StreakChart from './components/StreakChart.vue';\nimport { useProgress } from './composables/useProgress';\nconst modules = ref([{ id: 1, title: 'Intro' }, { id: 2, title: 'Loops' }]);\nconst { progress } = useProgress();\nfunction unlock(id) { progress.value[id] = true; }\nconst completed = computed(() => Object.values(progress.value).filter(Boolean).length);\nconst chartData = computed(() => ({ labels: modules.value.map(m => m.title), values: modules.value.map(m => progress.value[m.id] ? 1 : 0) }));\n</script>\n