// Streak chart component\n<template>\n  <canvas ref=\"chart\" />\n</template>\n<script setup>\nimport { ref, onMounted, defineProps } from 'vue';\nimport { Chart, LineElement, CategoryScale, LinearScale } from 'chart.js';\nconst props = defineProps({ data: Object });\nChart.register(LineElement, CategoryScale, LinearScale);\nconst chart = ref(null);\nonMounted(() => {\n  new Chart(chart.value, { type: 'line', data: { labels: props.data.labels, datasets: [{ data: props.data.values }] } });\n});\n</script>\n